import React, { Component } from "react";
import Axios from "axios";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      response: [],
      task: null,
      priority: null
    };

    this.onTaskChange = this.onTaskChange.bind(this);
  }

  componentDidMount() {
    Axios.get("http://localhost:3002/").then(
      response => {
        this.setState({ response: response.data.data });
      },
      error => {
        console.log(error);
      }
    );
  }

  handleSubmit = event => {
    event.preventDefault();
    const { task } = this.state;

    var data = {
      task: task,
      priority: priority
    };

    Axios.post("http://localhost:3002/addTask", data).then(
      response => {
        this.setState({ response: response.data.data });
      },
      error => {
        console.log(error);
      }
    );
  };

  handleDelete = event => {
    Axios.delete("http://localhost:3002/deleteTask/" + event.target.id).then(
      response => {
        this.setState({ response: response.data.data });
      },
      error => {
        console.log(error);
      }
    );
  };

  onTaskChange = event => {
    this.setState({ task: event.target.value });
  };

  render() {
    const items = this.state.response.map((item, key) => (
      <div className="card my-3 shadow" key={item._id}>
        <div className="card-body">{item.task}</div>
        <div className="card-footer">
          <button
            className="btn btn-danger"
            id={item._id}
            onClick={this.handleDelete}
          >
            Delete
          </button>
        </div>
      </div>
    ));

    return (
      <div className="container">
        <div className="row">
          <div className="col-md-3"></div>
          <div className="col-md-6 mt-3">
            <form
              className="border py-5 px-3 shadow"
              onSubmit={this.handleSubmit}
            >
              <h5 className="font-weight-bold text-center">Add Task</h5>
              <div className="form-group">
                <input
                  type="text"
                  className="form-control"
                  id="task"
                  name="task"
                  placeholder="Task"
                  onChange={this.onTaskChange}
                />
              </div>
              <select class="custom-select" onChange={this.onPriorityChange}>
                <option selected>Open this select menu</option>
                <option value="1">One</option>
                <option value="2">Two</option>
                <option value="3">Three</option>
              </select>
              <button type="submit" className="btn btn-primary mx-auto">
                Submit
              </button>
            </form>
            {items}
          </div>
          <div className="col-md-3"></div>
        </div>
      </div>
    );
  }
}

export default App;
