import React, { Component } from "react";
import Axios from "axios";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      response: [],
      task: null
    };

    this.onTaskChange = this.onTaskChange.bind(this);
  }

  componentDidMount() {
    Axios.get("http://localhost:3005/").then(
      response => {
        this.setState({ response: response.data.data });
      },
      error => {
        console.log(error);
      }
    );
  }

  handleSubmit = event => {
    event.preventDefault();
    const { task } = this.state;

    var data = {
      task: task
    };

    Axios.post("http://localhost:3005/addTask", data).then(
      response => {
        this.setState({ response: response.data.data });
      },
      error => {
        console.log(error);
      }
    );
  };

  handleDelete = event => {
    Axios.delete("http://localhost:3005/deleteTask/" + event.target._id).then(
      response => {
        this.setState({ response: response.data.data });
      },
      error => {
        console.log(error);
      }
    );
  };

  onTaskChange = event => {
    this.setState({ task: event.target.value });
  };

  render() {
    const items = this.state.response.map((item, key) => (
      <div className="card my-3 shadow" key={item._id}>
        <div className="card-body">{item.task}</div>
        <div className="card-footer">
          <button className="btn btn-danger" onClick={this.handleDelete}>
            Delete
          </button>
        </div>
      </div>
    ));

    return (
      <div className="container">
        <div className="d-flex justify-content-center mt-5">
          <form
            className="border py-5 px-3 shadow"
            onSubmit={this.handleSubmit}
          >
            <h5 className="font-weight-bold text-center">Add Task</h5>
            <div className="form-group">
              <input
                type="text"
                className="form-control"
                id="task"
                name="task"
                placeholder="Task"
                onChange={this.onTaskChange}
              />
            </div>
            <button type="submit" className="btn btn-primary mx-auto">
              Submit
            </button>
          </form>
          <ul>{items}</ul>
        </div>
      </div>
    );
  }
}

export default App;
